const DeleteStudent = () => {
  const [id, setId] = useState("");

  const handleDelete = () => {
    console.log("刪除學生座號:", id);
    // TODO: 呼叫 MongoDB API 進行刪除操作
  };

  return (
    <div>
      <h2>刪除學生</h2>
      <input placeholder="輸入座號" onChange={(e) => setId(e.target.value)} />
      <button onClick={handleDelete}>刪除</button>
    </div>
  );
};

export default DeleteStudent;
