import React, { useState } from "react";
import axios from "axios";
import "./styles.css";

const DeleteStudent = () => {
    const [sid, setSid] = useState(""); // 儲存座號
    const [student, setStudent] = useState(null); // 儲存學生資訊
    const [showPopup, setShowPopup] = useState(false); // 控制彈窗顯示
    const [message, setMessage] = useState(""); // 結果訊息

    // 查詢學生資訊
    const handleQuery = async () => {
        try {
            const numericSid = Number(sid); // 強制轉換 sid 為數字
            if (isNaN(numericSid)) {
                setMessage("座號必須是數字，請重新輸入！");
                return;
            }

            const response = await axios.get(`http://localhost:8080/api/v1/user/findBySid/${numericSid}`);
            console.log("後端回傳資料:", response.data);

            if (response.data.body) {
                setStudent(response.data.body);
                setShowPopup(true);
                setMessage("");
            } else {
                setMessage("查無此座號，請確認後再試！");
            }
        } catch (error) {
            console.error("查詢失敗:", error);
            setMessage("查無此座號，請確認後再試！");
        }
    };

    // 刪除學生
    const handleDelete = async () => {
        try {
            console.log("刪除的 ID:", student._id);
            await axios.delete(`http://localhost:8080/api/v1/user/deleteById?id=${student._id}`);
            setMessage("學生刪除成功！");
            setShowPopup(false);
            setStudent(null);
        } catch (error) {
            console.error("刪除失敗:", error);
            setMessage("刪除失敗，請稍後再試！");
        }
    };

    return (
        <div className="form-container">
            <h2>刪除學生</h2>
            <div>
                <input
                    type="text"
                    placeholder="座號查詢"
                    value={sid}
                    onChange={(e) => setSid(e.target.value)}
                />
                <button onClick={handleQuery} className="query-button">
                    查詢
                </button>
            </div>

            {/* 彈窗：確認學生資訊 */}
            {showPopup && student && (
                <>
                    {/* 遮罩背景 */}
                    <div className="overlay"></div>

                    {/* 彈窗 */}
                    <div className="popup">
                        <h3>確認學生資訊</h3>
                        <p>帳號: {student.userName || "未提供"}</p>
                        <p>座號: {student.sid || "未提供"}</p>
                        <p>姓名: {student.name || "未提供"}</p>
                        <p>院系: {student.department || "未提供"}</p>
                        <p>Email: {student.Email || "未提供"}</p>
                        <div>
                            <button onClick={handleDelete} className="delete-button">
                                刪除
                            </button>
                            <button onClick={() => setShowPopup(false)} className="cancel-button">
                                取消
                            </button>
                        </div>
                    </div>
                </>
            )}

            {/* 結果訊息 */}
            {message && <p>{message}</p>}
        </div>
    );
};

export default DeleteStudent;
