import React, { useEffect, useState } from "react";
import axios from "axios";
import "./styles.css";

const StudentList = () => {
  const [students, setStudents] = useState([]); // 學生資料狀態
  const [loading, setLoading] = useState(true); // 載入狀態
  const [error, setError] = useState(null); // 錯誤狀態

  // 從 API 獲取學生資料
  useEffect(() => {
    axios
      .get("http://localhost:8080/api/v1/user/findAll")
      .then((response) => {
        console.log("API 返回資料:", response.data);
        if (Array.isArray(response.data)) {
          setStudents(response.data); // 如果返回的是陣列
        } else if (response.data && Array.isArray(response.data.data)) {
          setStudents(response.data.data); // 如果資料包在 data 屬性裡
        } else {
          console.error("API 資料格式不符合預期:", response.data);
        }
      })
      .catch((err) => {
        console.error("Error fetching data:", err);
        setError("無法取得資料，請稍後再試！");
      });
  }, []);

  return (
    <div>
      <h2 style={{ textAlign: "center" }}>學生列表</h2>
      {loading && <p style={{ textAlign: "center" }}>資料載入中...</p>}
      {error && <p style={{ textAlign: "center", color: "red" }}>{error}</p>}

      <div className="card-container">
        {Array.isArray(students) && students.map((student) => (
          <div key={student.id} className="card">
            <p>帳號: {student.account || "未提供"}</p>
            <p>座號: {student.id || "未提供"}</p>
            <p>姓名: {student.name || "未提供"}</p>
            <p>院系: {student.department || "未提供"}</p>
            <p>年級: {student.grade || "未提供"}</p>
            <p>班級: {student.class || "未提供"}</p>
            <p>Email: {student.email || "未提供"}</p>
            <p>缺席次數: {student.absence || 0}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
export default StudentList;
