import React, { useState } from "react";

const UpdateStudentForm = () => {
  const [id, setId] = useState("");
  const [formData, setFormData] = useState({
    name: "",
    department: "",
    grade: "",
    class: "",
    email: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleUpdate = () => {
    console.log("更新學生座號:", id, "更新資料:", formData);
    // TODO: 呼叫 MongoDB API 進行更新操作
  };

  return (
    <div>
      <h2>更新學生資料</h2>
      <input placeholder="座號" onChange={(e) => setId(e.target.value)} />
      <input name="name" placeholder="姓名" onChange={handleChange} />
      <input name="department" placeholder="院系" onChange={handleChange} />
      <input name="grade" placeholder="年級" onChange={handleChange} />
      <input name="class" placeholder="班級" onChange={handleChange} />
      <input name="email" placeholder="Email" onChange={handleChange} />
      <button onClick={handleUpdate}>更新</button>
    </div>
  );
};

export default UpdateStudentForm;
