import React, { useState } from "react";

const AddStudentForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    department: "",
    grade: "",
    class: "",
    email: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("新增學生:", formData);
    // TODO: 呼叫 MongoDB API 進行資料儲存
  };

  return (
    <div>
      <h2>新增學生</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="姓名" onChange={handleChange} />
        <input name="department" placeholder="院系" onChange={handleChange} />
        <input name="grade" placeholder="年級" onChange={handleChange} />
        <input name="class" placeholder="班級" onChange={handleChange} />
        <input name="email" placeholder="Email" onChange={handleChange} />
        <button type="submit">新增</button>
      </form>
    </div>
  );
};

export default AddStudentForm;
