import React from "react";
import "./styles.css"; // 引入樣式

// 假設這是從後端 MongoDB 獲取的學生資料
const students = [
  {
    id: 1,
    account: "tkuee0787",
    name: "呱呱呱",
    department: "電機工程系",
    grade: "四年級",
    class: "A",
    email: "tkuee0787@tkuim.com",
    absence: 0,
  },
  {
    id: 2,
    account: "tkuee1234",
    name: "小明",
    department: "資訊工程系",
    grade: "三年級",
    class: "B",
    email: "xiaoming@tkuim.com",
    absence: 2,
  },
  {
    id: 3,
    account: "tkuee5678",
    name: "小華",
    department: "機械工程系",
    grade: "二年級",
    class: "C",
    email: "xiaohua@tkuim.com",
    absence: 1,
  },
  // 你可以繼續添加更多學生資料
];

const StudentList = () => {
  return (
    <div>
      <h2 style={{ textAlign: "center" }}>學生列表</h2>
      <div className="card-container">
        {students.map((student) => (
          <div key={student.id} className="card">
            <p>帳號: {student.account}</p>
            <p>座號: {student.id}</p>
            <p>姓名: {student.name}</p>
            <p>院系: {student.department}</p>
            <p>年級: {student.grade}</p>
            <p>班級: {student.class}</p>
            <p>Email: {student.email}</p>
            <p>缺席次數: {student.absence}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default StudentList;
