import React, { useState } from "react";
import axios from "axios";
import "./styles.css";

const UpdateStudentForm = () => {
  const [sid, setSid] = useState(""); // 輸入的座號
  const [student, setStudent] = useState(null); // 學生資料
  const [showPopup, setShowPopup] = useState(false); // 控制彈窗
  const [message, setMessage] = useState(""); // 狀態訊息
  const [name, setName] = useState(""); // 新的名字

  // 查詢學生資料
  const handleQuery = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/v1/user/findBySid/${sid}`);
      console.log("查詢成功:", response.data);
      setStudent(response.data.body); // 設定學生資料
      setName(response.data.body.name); // 初始化名字欄位
      setShowPopup(true); // 打開彈窗
      setMessage("");
    } catch (error) {
      console.error("查詢失敗:", error);
      setMessage("查無此座號，請確認後再試！");
    }
  };

  // 更新學生名字
  const handleUpdate = async () => {
    try {
      const updatedData = { id: student._id, name };
      const response = await axios.put(
        "http://localhost:8080/api/v1/user/updateNameById",
        updatedData
      );
      console.log("更新成功:", response.data);
      setMessage("學生資料更新成功！");
      setShowPopup(false);
      setStudent(null);
    } catch (error) {
      console.error("更新失敗:", error);
      setMessage("更新失敗，請稍後再試！");
    }
  };

  return (
    <div className="form-container">
      <h2>更新資料</h2>
      <div>
        <input
          type="text"
          placeholder="座號查詢"
          value={sid}
          onChange={(e) => setSid(e.target.value)}
        />
        <button onClick={handleQuery} className="query-button">
          查詢
        </button>
      </div>

      {/* 彈窗顯示學生資料 */}
      {showPopup && student && (
        <>
          <div className="overlay"></div>
          <div className="popup">
            <h3>編輯學生資料</h3>
            <p>帳號: {student.userName}</p>
            <p>座號: {student.sid}</p>
            <p>院系: {student.department}</p>
            <p>Email: {student.Email}</p>
            {/* 名字可編輯 */}
            <div>
              <label>名字:</label>
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
            <div className="button-group">
              <button onClick={handleUpdate} className="update-button">
                更新
              </button>
              <button onClick={() => setShowPopup(false)} className="cancel-button">
                取消
              </button>
            </div>
          </div>
        </>
      )}

      {/* 結果訊息 */}
      {message && <p>{message}</p>}
    </div>
  );
};

export default UpdateStudentForm;
