import React, { useState } from "react";
import axios from "axios";
import "./styles.css";

const DeleteStudent = () => {
  const [sid, setSid] = useState(""); // 儲存座號
  const [student, setStudent] = useState(null); // 儲存學生資訊
  const [showPopup, setShowPopup] = useState(false); // 控制彈窗顯示
  const [message, setMessage] = useState(""); // 結果訊息

  // 查詢學生資訊
  const handleQuery = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/v1/user/findBySid/:${sid}`);
      setStudent(response.data.body); // 設定學生資訊
      setShowPopup(true); // 顯示彈窗
    } catch (error) {
      console.error("查詢失敗:", error);
      setMessage("查無此座號，請確認後再試！");
    }
  };

  // 刪除學生
  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:8080/api/v1/user/deleteById?id=${student.id}`);
      setMessage("學生刪除成功！");
      setShowPopup(false); // 關閉彈窗
      setStudent(null); // 清除學生資訊
    } catch (error) {
      console.error("刪除失敗:", error);
      setMessage("刪除失敗，請稍後再試！");
    }
  };

  return (
    <div className="form-container">
      <h2>刪除學生</h2>
      <div>
        <input
          type="text"
          placeholder="座號查詢"
          value={sid}
          onChange={(e) => setSid(e.target.value)}
        />
        <button onClick={handleQuery} className="query-button">
          查詢
        </button>
      </div>

      {/* 彈窗：確認學生資訊 */}
      {showPopup && student && (
        <div className="popup">
          <h3>確認學生資訊</h3>
          <p>帳號: {student.userName}</p>
          <p>座號: {student.sid}</p>
          <p>姓名: {student.name}</p>
          <p>院系: {student.department}</p>
          <p>Email: {student.Email}</p>
          <div>
            <button onClick={handleDelete} className="delete-button">
              刪除
            </button>
            <button onClick={() => setShowPopup(false)} className="cancel-button">
              取消
            </button>
          </div>
        </div>
      )}

      {/* 結果訊息 */}
      {message && <p>{message}</p>}
    </div>
  );
};

export default DeleteStudent;
