import React, { useState } from "react";
import StudentList from "./components/StudentList";
import AddStudentForm from "./components/AddStudentForm";
import DeleteStudent from "./components/DeleteStudent";
import UpdateStudentForm from "./components/UpdateStudentForm";
import "./components/styles.css";

function App() {
  const [view, setView] = useState("list");

  return (
    <div>
      <nav>
        <button onClick={() => setView("list")}>所有學生</button>
        <button onClick={() => setView("add")}>新增學生</button>
        <button onClick={() => setView("delete")}>刪除學生</button>
        <button onClick={() => setView("update")}>更新資料</button>
      </nav>
      <div>
        {view === "list" && <StudentList />}
        {view === "add" && <AddStudentForm />}
        {view === "delete" && <DeleteStudent />}
        {view === "update" && <UpdateStudentForm />}
      </div>
    </div>
  );
}

export default App;
